import React from 'react';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const navigate = useNavigate();

  const handleLoginChoice = (role) => {
    // Redirect user based on their role choice
    navigate(`/${role}-login`);
  };

  return (
    <div>
      <h1>Welcome to MedVerify</h1>
      <p>Select your role to login:</p>
      
      <button onClick={() => handleLoginChoice('user')}>User Login</button>
      <button onClick={() => handleLoginChoice('manufacturer')}>Manufacturer Login</button>
      <button onClick={() => handleLoginChoice('distributor')}>Distributor Login</button>
    </div>
  );
};

export default HomePage;
